def decode_flag(memory: bytes):
    # Long string assigned to v3
    v3 = (
        "GQMXGTNDCWCABEGWGBKDFJMJTBWFACYMMSHFFTNZMHGGXQEUZHVFTCPJCKUEAMGBSXABZRMTKQCFUFXWFJMZFBMFTMDXZMVZXSRPQTCMFMBKNVMVXZRQ"
        "YQBGCQYYNVSNFGEWHKBDZJMXZSFEAUVSEPGQQYTKJNQMEEFHZQCFSXFDKVPSQMUPTJSMCRGNJZKCDJDQNSHTGPBMJUKUDQWMJUKMEYQFPZGXHCCTNPHY"
        "BUEQXSYCEMWQAHMVPNKYTAY"
    )

    # Simulate MEMORY[0x4000000] = memory[0x60]
    addr1 = memory
    addr2 = memory[0x80]

    offset_v2 = addr1 & 0x7
    offset_v1 = addr2 & 0x77

    v2 = v3[offset_v2:offset_v2 + 3]
    v1 = v3[offset_v1:offset_v1 + 3]

    return f"FLAG-{v2}{v1}"

